// src/api.js
import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:3000/api', // URL del backend, verifica que sea correcta
  headers: {
    'Authorization': `Bearer ${localStorage.getItem('token')}`,
  },
});


export default api;
