import React, { useState, useEffect } from 'react';
import api from '../api'; // Asegúrate de que api.js esté configurado correctamente
import { useNavigate } from 'react-router-dom';

const CoursesPage = () => {
  const [courses, setCourses] = useState([]);
  const [newCourse, setNewCourse] = useState({ title: '', description: '', duration: '', instructor: '' });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Obtener los cursos del usuario
    const fetchCourses = async () => {
      try {
        const response = await api.get('/courses');
        setCourses(response.data);
      } catch (err) {
        console.error('Error al obtener cursos:', err);
        setError('Error al cargar los cursos');
      }
    };

    fetchCourses();
  }, []);

  // Función modificada para crear un nuevo curso con validación de datos
  const handleCreateCourse = async (e) => {
    e.preventDefault();

    if (!newCourse.title || !newCourse.description || !newCourse.duration || !newCourse.instructor) {
      setError('Por favor, completa todos los campos.');
      return;
    }

    try {
      const response = await api.post('/courses', newCourse);
      setCourses([...courses, response.data]); // Agregar el nuevo curso al estado
      setNewCourse({ title: '', description: '', duration: '', instructor: '' }); // Limpiar el formulario
    } catch (err) {
      console.error('Error al crear el curso:', err);
      setError('Error al crear el curso');
    }
  };

  return (
    <div>
      <h1>Mis Cursos</h1>

      {error && <p>{error}</p>}

      <ul>
        {courses.map(course => (
          <li key={course._id}>
            <h2>{course.title}</h2>
            <p>{course.description}</p>
          </li>
        ))}
      </ul>

      <h2>Crear un Nuevo Curso</h2>
      <form onSubmit={handleCreateCourse}>
        <input
          type="text"
          placeholder="Título del curso"
          value={newCourse.title}
          onChange={(e) => setNewCourse({ ...newCourse, title: e.target.value })}
        />
        <textarea
          placeholder="Descripción del curso"
          value={newCourse.description}
          onChange={(e) => setNewCourse({ ...newCourse, description: e.target.value })}
        />
        <input
          type="number"
          placeholder="Duración del curso (en horas)"
          value={newCourse.duration}
          onChange={(e) => setNewCourse({ ...newCourse, duration: e.target.value })}
        />
        <input
          type="text"
          placeholder="Instructor"
          value={newCourse.instructor}
          onChange={(e) => setNewCourse({ ...newCourse, instructor: e.target.value })}
        />
        <button type="submit">Crear Curso</button>
      </form>
    </div>
  );
};

export default CoursesPage;
